CREATE TABLE "germline_cnv" (
	"CNV.ID" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"Sample" VARCHAR(255) NOT NULL,
	"Correlation" FLOAT NOT NULL,
	"N.comp" INTEGER NOT NULL,
	"N.exons" INTEGER NOT NULL,
	"CNV.type" CHAR(1) NOT NULL,
	"Start" INTEGER NOT NULL,
	"End" INTEGER NOT NULL,
	"Chromosome" INTEGER NOT NULL,
	"Genmoics.ID" VARCHAR(255) NOT NULL,
	"BF" FLOAT NOT NULL,
	"Reads.ratio" FLOAT NOT NULL,
	"Gene" VARCHAR(255) NOT NULL,
	"Exon.start" VARCHAR(255) NOT NULL,
	"Exon.end" VARCHAR(255) NOT NULL,
	"Overlap" FLOAT NOT NULL,
	"US_ratio" FLOAT NOT NULL,
	"Cofidence" VARCHAR(255) NOT NULL,
	"Genotype" VARCHAR(255) NOT NULL,
	"INFO" VARCHAR(255) NOT NULL,
	"Transcript" VARCHAR(255) NOT NULL,
	PRIMARY KEY("Sample")
);




CREATE TABLE "Normal_final_vcf" (
	"#CHROM" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"POS" INTEGER NOT NULL,
	"ID" CHAR(1) NOT NULL,
	"REF" VARCHAR(255) NOT NULL,
	"ALT" VARCHAR(255) NOT NULL,
	"QUAL" FLOAT NOT NULL,
	"FILTER" VARCHAR(255) NOT NULL,
	"INFO" VARCHAR(255) NOT NULL,
	"FORMAT" VARCHAR(255) NOT NULL,
	"SampleID" VARCHAR(255) NOT NULL,
	PRIMARY KEY("#CHROM", "POS", "REF", "ALT")
);




CREATE TABLE "Transcript" (
	"#gene_HGNC" VARCHAR(255) NOT NULL UNIQUE,
	"transcript" VARCHAR(255) NOT NULL,
	PRIMARY KEY("#gene_HGNC")
);




